import { View, Text, StyleSheet } from 'react-native'
import React, { useEffect, useState } from 'react'
import Menuc from '../component/Menuc'
import { TouchableOpacity } from 'react-native-gesture-handler'
import { useNavigation } from '@react-navigation/native'
import { Audio } from 'expo-av'

const Menu = () => {
  const [sound, setSound] = useState<Audio.Sound | null>(null);

  // Function to play the sound
  async function playSound() {
    console.log('Loading Sound');
    const { sound } = await Audio.Sound.createAsync(
      require('../assets/audio/menu.mp3')
    );
    setSound(sound);

    console.log('Playing Sound');
    await sound.playAsync();
  }

  // Automatically play sound when the component mounts
  useEffect(() => {
    playSound(); // Call playSound when component mounts

    return () => {
      if (sound) {
        console.log('Unloading Sound');
        sound.unloadAsync(); // Clean up sound when component unmounts
      }
    };
  }, []);


  const navigation = useNavigation();

    const handleSingleTap = () => {
        navigation.navigate('Menu');
    };

    const handleDoubleTap = () => {
        navigation.navigate('Admin');
    };

    const handleLongPress = () => {
        navigation.navigate('DetectObject');
    };
  return (
    <View>
      <Menuc/>
          <View  style={styles.buttonContainer}>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Menu')}>
                    <Text style={styles.buttonText}>Sciencetific </Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Admin')}>
                    <Text style={styles.buttonText}>Geomatric</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('DetectObject')}>
                    <Text style={styles.buttonText}>Currency</Text>
                </TouchableOpacity>
          </View>     
    </View>
  )
}

export default Menu

const styles = StyleSheet.create({
  container: {
      flex: 1,
      backgroundColor: '#D6D6D6', 
      justifyContent: 'center',
      alignItems: 'center',
  },
  micee: {
      justifyContent: 'center',
      alignItems: 'center',
      flex: 1,
  },
  mice: {
      width: 500, 
      height: 500,
  },
  buttonContainer: {
      marginBottom: 50,
      width: '100%',
      justifyContent: 'space-around',
      alignItems: 'center',
      flexDirection: 'row',
      paddingTop:60
  },
  button: {
      backgroundColor: 'black',
      padding: 10,
      borderRadius: 5,
      width:110,
      height:90,
      justifyContent:'center'
  },
  buttonText: {
      color: '#fff',
      fontSize: 16,
      justifyContent:'center'
  },
});
