import { View, StyleSheet } from 'react-native';
import React, { useEffect, useState } from 'react';
import Logoview from './src/component/Logoview';
import Texta from './src/component/Texta';
import Cont from './src/component/Cont';
import { Audio } from 'expo-av';

const App = () => {
  const [sound, setSound] = useState<Audio.Sound | null>(null);

  // Function to play the sound
  async function playSound() {
    console.log('Loading Sound');
    const { sound } = await Audio.Sound.createAsync(
      require('./src/assets/audio/welcome.mp3')
    );
    setSound(sound);

    console.log('Playing Sound');
    await sound.playAsync();
  }

  // Automatically play sound when the component mounts
  useEffect(() => {
    playSound(); // Call playSound when component mounts

    return () => {
      if (sound) {
        console.log('Unloading Sound');
        sound.unloadAsync(); // Clean up sound when component unmounts
      }
    };
  }, []);

  return (
    <View style={styles.container}>
      <Logoview />
      <Cont />
      <Texta />
    </View>
  );
};

export default App;

const styles = StyleSheet.create({
  container: {

  },
});
