import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Alert } from 'react-native';

const MyForm = ({ selectedOId }) => {
  const [formData, setFormData] = useState({
    
    name: '',
    warnings: ''
  });

  const handleChange = (name, value) => {
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://192.168.137.1:3000/instructions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          object_id: selectedOId,
          instruction: formData.name,
          warnings: formData.warnings,
        }),
      });
  
      const result = await response.json();
      if (response.ok) {
        Alert.alert("Success", "Data submitted successfully!");
      } else {
        Alert.alert("Error", result.error || 'Failed to submit data.');
      }
    } catch (error) {
      Alert.alert("Error", 'Failed to connect to the server.');
    }
  };
  

  return (
    <View style={styles.container}>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>ID</Text>
        <TextInput
          style={styles.input}
          placeholder={selectedOId}
          value={selectedOId}
          onChangeText={(value) => handleChange('id', value)}
          placeholderTextColor="#888"
        />
      </View>

      <View style={styles.inputContainer}>
        <Text style={styles.label}>Description</Text>
        <TextInput
          style={styles.input}
          placeholder="Enter description"
          value={formData.name}
          onChangeText={(value) => handleChange('name', value)}
          placeholderTextColor="#888"
        />
      </View>

      <View style={styles.inputContainer}>
        <Text style={styles.label}>Warnings</Text>
        <TextInput
          style={styles.input}
          placeholder="Enter warnings"
          value={formData.warnings}
          onChangeText={(value) => handleChange('warnings', value)}
          placeholderTextColor="#888"
        />
      </View>

      <TouchableOpacity style={styles.button} onPress={handleSubmit}>
        <Text style={styles.buttonText}>Submit</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 20,
    backgroundColor: '#f9f9f9',
  },
  inputContainer: {
    marginBottom: 15,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  input: {
    height: 45,
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 8,
    paddingHorizontal: 10,
    backgroundColor: '#fff',
    fontSize: 16,
  },
  button: {
    backgroundColor: '#007BFF',
    paddingVertical: 12,
    borderRadius: 8,
    alignItems: 'center',
    marginTop: 20,
  },
  buttonText: {
    fontSize: 18,
    color: '#fff',
    fontWeight: 'bold',
  },
});

export default MyForm;
